	package Batch51JavaPrograms;
	
	public class Asgn_19Constructorover 
	{
		
		public  Asgn_19Constructorover (int a, int b)
		{
			int sum = a+ b ;
			System.out.println(sum);
			
		}
		public Asgn_19Constructorover (int a, byte b)
		{
			int sum = a+ b ;
			System.out.println(sum);
			
		}
	
		public static void main(String[] args) 
		{
			// TODO Auto-generated method stub
			Asgn_19Constructorover w1 = new Asgn_19Constructorover(20,60);
			Asgn_19Constructorover w2 = new Asgn_19Constructorover(20,50);
			
		}
	
	}
