package Batch51JavaPrograms;

public class Asgn_5Ifblock 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a= 20;
		int b= 30;
		
		
		if (a<b)
		{
			System.out.println("a is less than b");
		}
		if (a>b)
		{
			System.out.println("a is greater than b");
		}
		
		
	}

}
